function  JobPostingController($scope,$element,$http,$timeout){
	$scope.Min = [];
	$scope.Max = [];
	$scope.edit =1;
	$scope.existing_job = '------Copy From existing job----';
    $scope.jobpost = {
        'title':'',
        'code': '',
        'summary': '',
        'details': '',
        'skills': '',
        'location': '-select-',
        'industry': '-select-',
        'function': '-select-',
        'role': '-select-',
        'requirement': '-select-',
        'specialisation': '-select-',
        'nationality': '-select-',
        'name': '',
        'phone': '',
        'email': '',
        'profile': '',
        'min':'-min-',
        'max':'-max-',
    }
	$scope.init = function(csrf_token,id) {
		$scope.csrf_token = csrf_token;
		$scope.product_pdf = {};
    	$scope.product_pdf.src = "";
    	$scope.get_existing_jobs();
		get_countries($scope);
		get_nationalities($scope);
		get_industries($scope);
		get_functions($scope);
		get_education_required($scope);
		for(var i=0; i<=50; i++){
      	 	$scope.Min.push(i);
      	 	$scope.Max.push(i);
    	}	
    	console.log($scope.jobpost);
		
	}
	$scope.save_job = function(){
		var file = $scope.product_pdf.src;
		var edit =$scope.edit;
		params = {
                'jobpost':angular.toJson($scope.jobpost),
                "csrfmiddlewaretoken" : $scope.csrf_token,
            }
        console.log($scope.jobpost);
        var fd = new FormData();
        fd.append('product_pdf', $scope.product_pdf.src);
        for(var key in params){
          fd.append(key, params[key]);
        }
        if(edit == 1){
        	var url = "/recruiter/post-jobs/";
        }
        else{
        	var url = "/recruiter/post-jobs/edit/"+$scope.id+"/";
        }
        $http.post(url, fd, {
                transformRequest: angular.identity,
                headers: {'Content-Type': undefined
            	}
                
            }).success(function(data, status){
                console.log("Successfully Saved");
                $scope.id = data.id;
                $scope.edit = $scope.edit +1;  
          }).error(function(data, status){
              console.log(status);
        });
        
	}
	$scope.get_existing_jobs = function() {
		$http.get('/jobs/list/').success(function(data)
	    {
	        $scope.existing_jobs = data.existing_jobs;		
	    }).error(function(data, status)
	    {
	        console.log(data || "Request failed");
	    });
	}
	$scope.get_job_details = function(){
		$http.get('/jobs/details/'+$scope.existing_job+'/').success(function(data)
	    {
	    	$scope.existing_job_detail = data.existing_job_details;
	    	$scope.jobpost = data.existing_job_details[0];
	    	console.log($scope.jobpost);
	    }).error(function(data, status)
	    {
	        console.log(data || "Request failed");
	    });

	}
}
